import {Injectable} from '@angular/core';
import {DOCUMENT} from '@angular/common';
import {Subject} from 'rxjs/Subject';
import {CookieOptions, COOKIE_OPTIONS} from './cookie_options';

/**
 * Provides access to the HTTP cookies.
 * See: https://developer.mozilla.org/en-US/docs/Web/HTTP/Cookies
 */
export class Cookies {

  /**
   * The class decorators.
   * @type {Array}
   */
  static get annotations() {
    return [new Injectable];
  }

  /**
   * The constructor parameters.
   * @type {Array}
   */
  static get parameters() {
    return [COOKIE_OPTIONS, DOCUMENT];
  }

  /**
   * Initializes a new instance of the class.
   * @param {CookieOptions} cookieOptions The default cookie options.
   * @param {HTMLDocument} document The underlying HTML document.
   */
  constructor(cookieOptions, document) {

    /**
     * The default cookie options.
     * @type {CookieOptions}
     */
    this._defaults = cookieOptions;

    /**
     * The underlying HTML document.
     * @type {HTMLDocument}
     */
    this._document = document;

    /**
     * The handler of "changes" events.
     * @type {Subject<KeyValueChangeRecord>}
     */
    this._onChanges = new Subject;
  }

  /**
   * The default cookie options.
   * @type {CookieOptions}
   */
  get defaults() {
    return this._defaults;
  }

  /**
   * The keys of the cookies associated with the current document.
   * @type {string[]}
   */
  get keys() {
    let keys = this._document.cookie.replace(/((?:^|\s*;)[^=]+)(?=;|$)|^\s*|\s*(?:=[^;]*)?(?:\1|$)/g, '');
    return keys.length ? keys.split(/\s*(?:=[^;]*)?;\s*/).map(key => decodeURIComponent(key)) : [];
  }

  /**
   * The number of cookies associated with the current document.
   * @type {number}
   */
  get length() {
    return this.keys.length;
  }

  /**
   * The stream of "changes" events.
   * @type {Observable<KeyValueChangeRecord[]>}
   */
  get onChanges() {
    return this._onChanges.asObservable();
  }

  /**
   * Returns a new iterator that allows iterating the cookies associated with the current document.
   */
  *[Symbol.iterator]() {
    for (let key of this.keys) yield [key, this.get(key)];
  }

  /**
   * Removes all cookies associated with the current document.
   */
  clear() {
    let changes = this.keys.map(key => ({currentValue: null, key, previousValue: this.get(key)}));
    for (let key of this.keys) this._removeItem(key);
    this._onChanges.next(changes);
  }

  /**
   * Gets the value associated to the specified key.
   * @param {string} key The cookie name.
   * @param {*} defaultValue The default cookie value if it does not exist.
   * @return {string} The cookie value, or the default value if the item is not found.
   */
  get(key, defaultValue = null) {
    if (!this.has(key)) return defaultValue;

    try {
      let token = encodeURIComponent(key).replace(/[-.+*]/g, '\\$&');
      let scanner = new RegExp(`(?:(?:^|.*;)\\s*${token}\\s*\\=\\s*([^;]*).*$)|^.*$`);
      return decodeURIComponent(this._document.cookie.replace(scanner, '$1'));
    }

    catch (err) {
      return defaultValue;
    }
  }

  /**
   * Gets the deserialized value associated to the specified key.
   * @param {string} key The cookie name.
   * @param {*} defaultValue The default cookie value if it does not exist.
   * @return {*} The deserialized cookie value, or the default value if the item is not found.
   */
  getObject(key, defaultValue = null) {
    try {
      let value = this.get(key);
      return typeof value == 'string' ? JSON.parse(value) : defaultValue;
    }

    catch (err) {
      return defaultValue;
    }
  }

  /**
   * Gets a value indicating whether the current document has a cookie with the specified key.
   * @param {string} key The cookie name.
   * @return {boolean} `true` if the current document has a cookie with the specified key, otherwise `false`.
   */
  has(key) {
    let token = encodeURIComponent(key).replace(/[-.+*]/g, '\\$&');
    return new RegExp(`(?:^|;\\s*)${token}\\s*\\=`).test(this._document.cookie);
  }

  /**
   * Removes the value associated to the specified key.
   * @param {string} key The cookie name.
   * @param {CookieOptions} [options] The cookie options.
   */
  remove(key, options = this.defaults) {
    let previousValue = this.get(key);
    this._removeItem(key, options);
    this._onChanges.next([{currentValue: null, key, previousValue}]);
  }

  /**
   * Associates a given value to the specified key.
   * @param {string} key The cookie name.
   * @param {string} value The cookie value.
   * @param {CookieOptions|Date} [options] The cookie options, or the expiration date and time for the cookie.
   * @throws {TypeError} The specified key is invalid.
   */
  set(key, value, options = this.defaults) {
    if (!key.length || /^(domain|expires|max-age|path|secure)$/i.test(key)) throw new TypeError('Invalid cookie name.');

    let cookieValue = `${encodeURIComponent(key)}=${encodeURIComponent(value)}`;
    if (options instanceof Date) options = new CookieOptions(options, this.defaults.path, this.defaults.domain, this.defaults.secure);
    if (options.toString().length) cookieValue += `; ${options}`;

    let previousValue = this.get(key);
    this._document.cookie = cookieValue;
    this._onChanges.next([{currentValue: value, key, previousValue}]);
  }

  /**
   * Serializes and associates a given value to the specified key.
   * @param {string} key The cookie name.
   * @param {*} value The cookie value.
   * @param {CookieOptions|Date} [options] The cookie options, or the expiration date and time for the cookie.
   */
  setObject(key, value, options = this.defaults) {
    this.set(key, JSON.stringify(value), options);
  }

  /**
   * Returns a string representation of this object.
   * @return {string} The string representation of this object.
   */
  toString() {
    return this._document.cookie;
  }

  /**
   * Removes the value associated to the specified key.
   * @param {string} key The cookie name.
   * @param {CookieOptions} [options] The cookie options.
   */
  _removeItem(key, options = this.defaults) {
    if (!this.has(key)) return;

    let {domain, path} = options;
    let cookieOptions = new CookieOptions(0, path, domain);
    this._document.cookie = `${encodeURIComponent(key)}=; ${cookieOptions}`;
  }
}

import {NgModule} from '@angular/core';
import {CookieOptions} from './cookie_options';
import {Cookies} from './cookies';

/**
 * The cookie module.
 */
export class CookieModule {

  /**
   * The class decorators.
   * @type {Array}
   */
  static get annotations() {
    return [new NgModule({
      providers: [
        CookieOptions.provider,
        Cookies
      ]
    })];
  }
}

import {APP_BASE_HREF} from '@angular/common';
import {InjectionToken, Injector} from '@angular/core';

/**
 * An injection token representing the default cookie options.
 * @type {InjectionToken}
 */
export const COOKIE_OPTIONS = new InjectionToken('COOKIE_OPTIONS');

/**
 * Defines the attributes of a HTTP cookie.
 */
export class CookieOptions {

  /**
   * The service provider.
   * @type {object}
   */
  static get provider() {
    return {
      provide: COOKIE_OPTIONS,
      useFactory: injector => new CookieOptions(null, injector.get(APP_BASE_HREF, '/')),
      deps: [Injector]
    };
  }

  /**
   * Initializes a new instance of the class.
   * @param {Date|number|string} [expires] The expiration date and time for the cookie.
   * @param {string} [path] The path to which the cookie applies.
   * @param {string} [domain] The domain for which the cookie is valid.
   * @param {boolean} [secure] Value indicating whether to transmit the cookie over HTTPS only.
   */
  constructor(expires = null, path = '', domain = '', secure = false) {

    /**
     * The domain for which the cookie is valid.
     * @type {string}
     */
    this.domain = domain;

    /**
     * The expiration date and time for the cookie.
     * @type {Date}
     */
    this.expires = expires instanceof Date ? expires : null;
    if (Number.isInteger(expires) || typeof expires == 'string') this.expires = new Date(expires);

    /**
     * The path to which the cookie applies.
     * @type {string}
     */
    this.path = path;

    /**
     * Value indicating whether to transmit the cookie over HTTPS only.
     * @type {boolean}
     */
    this.secure = secure;
  }

  /**
   * Converts this object to a map in JSON format.
   * @return {object} The map in JSON format corresponding to this object.
   */
  toJSON() {
    return {
      domain: this.domain,
      expires: this.expires ? this.expires.toISOString() : null,
      path: this.path,
      secure: this.secure
    };
  }

  /**
   * Returns a string representation of this object.
   * @return {string} The string representation of this object.
   */
  toString() {
    let value = [];
    if (this.expires) value.push(`expires=${this.expires.toUTCString()}`);
    if (this.domain.length) value.push(`domain=${this.domain}`);
    if (this.path.length) value.push(`path=${this.path}`);
    if (this.secure) value.push('secure');
    return value.join('; ');
  }
}

export * from './cookie_module';
export * from './cookie_options';
export * from './cookies';

'use strict';

import {expect} from 'chai';
import CookieMock from 'ec.cookie-mock';
import {afterEach, describe, it} from 'mocha';
import {CookieOptions, Cookies} from '../src/index';

/**
 * @test {Cookies}
 */
describe('Cookies', () => {

  /**
   * @test {Cookies#keys}
   */
  describe('#keys', () => {
    it('should return an empty array if the current document has no associated cookie', () => {
      expect(new Cookies(new CookieOptions, new CookieMock).keys).to.be.an('array').that.is.empty;
    });

    it('should return the keys of the cookies associated with the current document', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';
      backend.cookie = 'bar=baz';

      let keys = new Cookies(new CookieOptions, backend).keys;
      expect(keys).to.be.an('array').and.have.lengthOf(2);
      expect(keys[0]).to.equal('foo');
      expect(keys[1]).to.equal('bar');
    });
  });

  /**
   * @test {Cookies#length}
   */
  describe('#length', () => {
    it('should return zero if the current document has no associated cookie', () => {
      expect(new Cookies(new CookieOptions, new CookieMock)).to.have.lengthOf(0);
    });

    it('should return the number of cookies associated with the current document', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';
      backend.cookie = 'bar=baz';
      expect(new Cookies(new CookieOptions, backend)).to.have.lengthOf(2);
    });
  });

  /**
   * @test {Cookies#onChanges}
   */
  describe('#onChanges', () => {
    let subscription;
    afterEach('cancel the subscription', () =>
      subscription.unsubscribe()
    );

    it('should trigger an event when a cookie is added', done => {
      let cookies = new Cookies(new CookieOptions, new CookieMock);
      subscription = cookies.onChanges.subscribe(changes => {
        expect(changes).to.be.an('array').and.have.lengthOf(1);

        let record = changes[0];
        expect(record).to.be.an('object');
        expect(record).to.have.property('key').that.equal('foo');
        expect(record).to.have.property('currentValue').that.equal('bar');
        expect(record).to.have.property('previousValue').that.is.null;

        done();
      });

      cookies.set('foo', 'bar');
    });

    it('should trigger an event when a cookie is updated', done => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';

      let cookies = new Cookies(new CookieOptions, backend);
      subscription = cookies.onChanges.subscribe(changes => {
        expect(changes).to.be.an('array').and.have.lengthOf(1);

        let record = changes[0];
        expect(record).to.be.an('object');
        expect(record).to.have.property('key').that.equal('foo');
        expect(record).to.have.property('currentValue').that.equal('baz');
        expect(record).to.have.property('previousValue').that.equal('bar');

        done();
      });

      cookies.set('foo', 'baz');
    });

    it('should trigger an event when a cookie is removed', done => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';

      let cookies = new Cookies(new CookieOptions, backend);
      subscription = cookies.onChanges.subscribe(changes => {
        expect(changes).to.be.an('array').and.have.lengthOf(1);

        let record = changes[0];
        expect(record).to.be.an('object');
        expect(record).to.have.property('key').that.equal('foo');
        expect(record).to.have.property('currentValue').that.is.null;
        expect(record).to.have.property('previousValue').that.equal('bar');

        done();
      });

      cookies.remove('foo');
    });

    it('should trigger an event when all the cookies are removed', done => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';
      backend.cookie = 'bar=baz';

      let cookies = new Cookies(new CookieOptions, backend);
      subscription = cookies.onChanges.subscribe(changes => {
        expect(changes).to.be.an('array').and.have.lengthOf(2);

        let record = changes[0];
        expect(record).to.be.an('object');
        expect(record).to.have.property('key').that.equal('foo');
        expect(record).to.have.property('currentValue').that.is.null;
        expect(record).to.have.property('previousValue').that.equal('bar');

        record = changes[1];
        expect(record).to.be.an('object');
        expect(record).to.have.property('key').that.equal('bar');
        expect(record).to.have.property('currentValue').that.is.null;
        expect(record).to.have.property('previousValue').that.equal('baz');

        done();
      });

      cookies.clear();
    });
  });

  /**
   * @test {Cookies#Symbol.iterator}
   */
  describe('#[Symbol.iterator]()', () => {
    it('should return a done iterator if the current document has no associated cookie', () => {
      let cookies = new Cookies(new CookieOptions, new CookieMock);
      let iterator = cookies[Symbol.iterator]();
      expect(iterator.next().done).to.be.true;
    });

    it('should return a value iterator if the current document has associated cookies', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';
      backend.cookie = 'bar=baz';

      let cookies = new Cookies(new CookieOptions, backend);
      let iterator = cookies[Symbol.iterator]();

      let next = iterator.next();
      expect(next.done).to.be.false;
      expect(next.value).to.be.an('array');
      expect(next.value[0]).to.equal('foo');
      expect(next.value[1]).to.equal('bar');

      next = iterator.next();
      expect(next.done).to.be.false;
      expect(next.value[0]).to.equal('bar');
      expect(next.value[1]).to.equal('baz');
      expect(iterator.next().done).to.be.true;
    });
  });

  /**
   * @test {Cookies#clear}
   */
  describe('#clear()', () => {
    it('should remove all the cookies associated with the current document', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';
      backend.cookie = 'bar=baz';

      let cookies = new Cookies(new CookieOptions, backend);
      cookies.clear();
      expect(backend.cookie).to.not.contain('foo');
      expect(backend.cookie).to.not.contain('bar');
    });
  });

  /**
   * @test {Cookies#get}
   */
  describe('#get()', () => {
    it('should properly get the cookies associated with the current document', () => {
      let backend = new CookieMock;
      let cookies = new Cookies(new CookieOptions, backend);

      expect(cookies.get('foo')).to.be.null;
      expect(cookies.get('foo', '123')).to.equal('123');

      backend.cookie = 'foo=bar';
      expect(cookies.get('foo')).to.equal('bar');

      backend.cookie = 'foo=123';
      expect(cookies.get('foo')).to.equal('123');
    });
  });

  /**
   * @test {Cookies#getObject}
   */
  describe('#getObject()', () => {
    it('should properly get the deserialized cookies associated with the current document', () => {
      let backend = new CookieMock;
      let cookies = new Cookies(new CookieOptions, backend);

      expect(cookies.getObject('foo')).to.be.null;
      expect(cookies.getObject('foo', {key: 'value'})).to.deep.equal({key: 'value'});

      backend.cookie = 'foo=123';
      expect(cookies.getObject('foo')).to.equal(123);

      backend.cookie = 'foo=%22bar%22';
      expect(cookies.getObject('foo')).to.equal('bar');

      backend.cookie = 'foo=%7B%22key%22%3A%22value%22%7D';
      expect(cookies.getObject('foo')).to.be.an('object')
        .and.have.property('key').that.equal('value');
    });

    it('should return the default value if the value can\'t be deserialized', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';

      let cookies = new Cookies(new CookieOptions, backend);
      expect(cookies.getObject('foo', 'defaultValue')).to.equal('defaultValue');
    });
  });

  /**
   * @test {Cookies#has}
   */
  describe('#has()', () => {
    it('should return `false` if the current document has an associated cookie with the specified key', () => {
      expect(new Cookies(new CookieOptions, new CookieMock).has('foo')).to.be.false;
    });

    it('should return `true` if the current document does not have an associated cookie with the specified key', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';

      let cookies = new Cookies(new CookieOptions, backend);
      expect(cookies.has('foo')).to.be.true;
      expect(cookies.has('bar')).to.be.false;
    });
  });

  /**
   * @test {Cookies#remove}
   */
  describe('#remove()', () => {
    it('should properly remove the cookies associated with the current document', () => {
      let backend = new CookieMock;
      backend.cookie = 'foo=bar';
      backend.cookie = 'bar=baz';

      let cookies = new Cookies(new CookieOptions, backend);
      cookies.remove('foo');
      expect(backend.cookie).to.not.contain('foo');
      expect(backend.cookie).to.contain('bar=baz');

      cookies.remove('bar');
      expect(backend.cookie).to.not.contain('bar');
    });
  });

  /**
   * @test {Cookies#set}
   */
  describe('#set()', () => {
    it('should properly set the cookies associated with the current document', () => {
      let backend = new CookieMock;
      let cookies = new Cookies(new CookieOptions, backend);
      expect(backend.cookie).to.not.contain('foo');

      cookies.set('foo', 'bar');
      expect(backend.cookie).to.contain('foo=bar');

      cookies.set('foo', '123');
      expect(backend.cookie).to.contain('foo=123');
    });

    it('should throw an error if the specified key is a reserved word', () => {
      let cookies = new Cookies(new CookieOptions, new CookieMock);
      expect(() => cookies.set('domain', 'foo')).to.throw(TypeError);
      expect(() => cookies.set('expires', 'foo')).to.throw(TypeError);
      expect(() => cookies.set('max-age', 'foo')).to.throw(TypeError);
      expect(() => cookies.set('path', 'foo')).to.throw(TypeError);
      expect(() => cookies.set('secure', 'foo')).to.throw(TypeError);
    });
  });

  /**
   * @test {Cookies#setObject}
   */
  describe('#setObject()', () => {
    it('should properly serialize and set the cookies associated with the current document', () => {
      let backend = new CookieMock;
      let cookies = new Cookies(new CookieOptions, backend);
      expect(backend.cookie).to.not.contain('foo');

      cookies.setObject('foo', 123);
      expect(backend.cookie).to.contain('foo=123');

      cookies.setObject('foo', 'bar');
      expect(backend.cookie).to.contain('foo=%22bar%22');

      cookies.setObject('foo', {key: 'value'});
      expect(backend.cookie).to.contain('foo=%7B%22key%22%3A%22value%22%7D');
    });

    it('should throw an error if the specified key is a reserved word', () => {
      let cookies = new Cookies(new CookieOptions, new CookieMock);
      expect(() => cookies.setObject('domain', 'foo')).to.throw(TypeError);
      expect(() => cookies.setObject('expires', 'foo')).to.throw(TypeError);
      expect(() => cookies.setObject('max-age', 'foo')).to.throw(TypeError);
      expect(() => cookies.setObject('path', 'foo')).to.throw(TypeError);
      expect(() => cookies.setObject('secure', 'foo')).to.throw(TypeError);
    });
  });

  /**
   * @test {Cookies#toString}
   */
  describe('#toString()', () => {
    it('should return an empty string for a newly created instance', () => {
      expect(String(new Cookies(new CookieOptions, new CookieMock))).to.be.empty;
    });

    it('should return a format like "<key>=<value>; expires=<expires>; domain=<domain>; path=<path>; secure" for an initialized instance', () => {
      let cookies = new Cookies(new CookieOptions(null, '/path', 'domain.com', true), new CookieMock);
      cookies.set('foo', 'bar');
      expect(String(cookies)).to.equal('foo=bar; domain=domain.com; path=/path; secure');

      cookies.set('bar', 'baz', new Date(Date.now() + 3600000));
      expect(String(cookies)).to.contain('bar=baz; expires=').and.contain('domain=domain.com; path=/path; secure');
    });
  });
});

'use strict';

import {expect} from 'chai';
import {describe, it} from 'mocha';
import {CookieOptions} from '../src/index';

/**
 * @test {CookieOptions}
 */
describe('CookieOptions', () => {

  /**
   * @test {CookieOptions#toJSON}
   */
  describe('#toJSON()', () => {
    it('should return a map with default values for a newly created instance', () => {
      expect((new CookieOptions).toJSON()).to.be.an('object').that.deep.equal({
        expires: null,
        path: '',
        domain: '',
        secure: false
      });
    });

    it('should return a non-empty map for an initialized instance', () => {
      expect(new CookieOptions(0, '/path', 'domain.com', true).toJSON()).to.be.an('object').that.deep.equal({
        expires: '1970-01-01T00:00:00.000Z',
        path: '/path',
        domain: 'domain.com',
        secure: true
      });
    });
  });

  /**
   * @test {CookieOptions#toString}
   */
  describe('#toString()', () => {
    it('should return an empty string for a newly created instance', () => {
      expect(String(new CookieOptions)).to.be.empty;
    });

    it('should return a format like "expires=<expires>; domain=<domain>; path=<path>; secure" for an initialized instance', () => {
      expect(String(new CookieOptions(0, '/path', 'domain.com', true)))
        .to.equal('expires=Thu, 01 Jan 1970 00:00:00 GMT; domain=domain.com; path=/path; secure');
    });
  });
});
